INSERT INTO `articles` (`ID`, `TITLE`, `BODY`, `CREATEDAT`, `MENUITEM`, `BACKGROUND`) VALUES
	(1, 'Glavna stran', 'Vodič po Rusiji', '2018-10-15 20:07:50', 'Glavna stran', 'Tajga.jpg'),
	(2, 'Nekaj o vodiču', 'Pozdravljeni! Jaz sem Maxim Tursunov. Rodil sem se v Rusiji, in sicer v njeni prestolnici Moskvi. Povedal vam bom o svoji veliki državi.', '2018-10-15 19:14:57', 'O vodiču', 'Karelija.jpg'),
	(3, 'Mesta', '<b>Moskva</b>\r\n<p>Moskva je glavno mesto in največje mesto v Rusiji (4.770 km<sup>2</sup>).\r\nRazprostira se na 880 km<sup>2</sup> ob reki Moskvi. Prebivalstvo naglo narašča, ocena za leto 2004 je 11,2 milijona prebivalcev.\r\nMesto leži v zveznem okrožju Osrednja Rusija (ki sicer geografsko leži na skrajnem zahodu države). Moskva je bila tudi glavno mesto Sovjetske zveze in Moskovitske države, predimperialne Rusije.</p>\r\n<img width="200px" length="200px" src="./img/Grb+Moskve.png" />\r\n\r\n<p><b>Sankt-Peterburg</b></p>\r\n<p>Sankt Peterburg je s 4.700.000 prebivalci drugo največje mesto v Ruski federaciji. V bližnji okolici pa jih živi še deset milijonov. Leži na strateško izjemno pomembnem mestu, kjer se reka Neva deltasto izliva v Finski zaliv in dejansko simbolizira rusko okno v Evropo. Mesto je bilo ustanovljeno 27. maja 1703 na ukaz carja Petra I. Velikega, ki je mestu z najemom tujih – predvsem italijanskih – arhitektov dal evropsko noto. Je eno najpomembnejših ruskih pristanišč, zato je z gospodarskega vidika od nekdaj izjemno pomembno mesto.\r\nOd 18. (31.) avgusta 1914 do 26. januarja 1924 je bilo mesto poimenovano Petrograd, od 26. januarja 1924 do 6. septembra 1991 pa Leningrad.</p>\r\n<img width="200px"  length="200px" src="./img/Grb+Sankta+Peterburga.png" />', '2018-10-15 19:14:59', 'Mesta', 'Moskva.png'),
	(4, 'Geografija', 'Rusija meji na zahodu na Norveško, Finsko, Estonijo, Latvijo, Litvo, Poljsko (zadnji dve prek enklave na obali Baltskega morja), Belorusijo, Ukrajino, na jugu pa na Gruzijo, Azerbajdžan, Kazahstan, Mongolijo, Kitajsko, Severno Korejo, na jugovzhodu pa ima morsko mejo z Japonsko. Njena obsežna obala se na odprtem morju razteza od Arktičnega oceana do severnega Pacifiškega oceana; prek bolj zaprtih vodnih površin, Črnega morja, Baltskega morja in Kaspijskega jezera, je obrnjena tudi proti več drugim državam.\r\n<img src="./img/Zemljevid+Rusije.png" />', '2018-10-15 20:01:01', 'Geografija', 'Bajkalsko morje.jpg'),
	(5, 'Podnebje', '<p>Podnebje je večinoma celinsko, na severu subarktično in arktično. Zimske temperature dosežejo najnižje vrednosti v vzhodni Sibiriji (v Ojmjakonu so leta 1933 izmerili -71,1 °C).</p>\r\n<p>Količina padavin se zmanjšuje od zahoda proti vzhodu. Na obali Črnega morja je ozek pas podnebja, ki je močno podobno sredozemskemu.</p>', '2018-10-28 11:58:49', 'Podnebje', 'Stepa.jpg'),
	(6, 'Poselitev', '<p>Rusija je večnacionalna država, kjer živi vsaj 120 narodov, od katerih je največ Rusov - po stanju s 1. majem 2007 115.889.807 (79,83 % prebivalstva). Gostota prebivalstva znaša 8,3/km<sup>2</sup>.</p>\r\n<p>Prebivalstvo je razporejeno neenakomerno. Tri četrtine prebivalstva živijo v evropskem delu, kjer so najpogosteje poseljena osrednja območja. V Sibiriji je bolj poseljen le pas ob transsibirski železnici.</p>\r\n<img src="./img/Poselitev+1.jpg" />\r\n<img src="./img/Poselitev+2.jpg" />\r\n<img src="./img/Poselitev+3.jpg" />', '2018-10-28 12:01:16', 'Poselitev', 'Kaliningrad.jpg'),
	(7, 'Gospodarstvo', '<b>Rudarstvo</b>\r\n<p>Rusija je bogata z rudami. Večina zalog je v azijskem delu države.</p>\r\n<img src="./img/Rudarstvo.jpg" />\r\n\r\n<p><b>Energetika</b></p>\r\n<p>Energetika je ena izmed strateških panog Rusije, saj Evropa uvozi polovico energije prav iz te države.</p>\r\n<img src="./img/Energetika.jpg" />\r\n\r\n<p><b>Industrija</b></p>\r\n<p>V času Sovjetske zveze se je razvijala težka industrija, predvsem železarstva in jeklarstva. Še leta 1989 so bili prvi na svetu. Proizvodnja od leta 1991 nazaduje. Do 1994 se je zmanjšala za skoraj polovico. Industrija je danes zastarela in povzroča onesnaževanje okolja.<p>\r\n<img src="./img/Industrija.jpg" />\r\n\r\n<p><b>Nafta in plin</b></p>\r\n<p>Največja nahajališča nafte in zemeljskega plina so v Zahodnosibirskem nižavju. Rusija je po načrpanem zemeljskem plinu na prvem mestu, po nafti pa na tretjem mestu na svetu. Plin in nafto pošilja po naftovodih in plinovodih prek Urala tudi v druge evropske države!</p>\r\n\r\n<p><b>Kmetijstvo</b></p>\r\n<p>Zaradi podnebnih omejitev v večjem delu države je za kmete na voljo malo obdelovalnih površin. Največ pridelujejo žita, predvsem pšenico, ječmen, rž in koruzo. Od živinoreje sta najpomembnejši govedoreja in prašičereja.</p>', '2018-10-28 12:04:35', 'Gospodarstvo', 'Srednjesibirska ravnina.jpg'),
	(8, 'Viri', '<div class=\'longlink\'><a href=\'https://sl.wikipedia.org/wiki/Rusija\'>https://sl.wikipedia.org/wiki/Rusija</a></div>\r\n<div class=\'longlink\'><a href=\'https://www.gismeteo.ru\'>https://www.gismeteo.ru</a></div>\r\n<div class=\'longlink\'><a href=\'https://yandex.ru/maps/?ll=37.841874%2C55.698687&z=9\'>https://yandex.ru/maps/?ll=37.841874%2C55.698687&z=9</a></div>\r\n\r\n<b>Slike:</b>\r\n<div class=\'longlink\'><a href=\'https://www.google.si/search?biw=1366&bih=656&tbm=isch&sa=1&ei=EeC0W_7qFsnFwAKZpoPIBA&q=kmetijstvo+v+rusiji&oq=kmetijstvo+v+rusiji&gs_l=img.3...904566.909581.0.910161.11.11.0.0.0.0.153.1334.0j10.10.0....0...1c.1.64.img..3.0.0....0.DjES3EcOlwA#imgrc=E52Fj6eX26vrLM:\'>https://www.google.si/search?biw=1366&bih=656&tbm=isch&sa=1&ei=EeC0W_7qFsnFwAKZpoPIBA&q=kmetijstvo+v+rusiji&oq=kmetijstvo+v+rusiji&gs_l=img.3...904566.909581.0.910161.11.11.0.0.0.0.153.1334.0j10.10.0....0...1c.1.64.img..3.0.0....0.DjES3EcOlwA#imgrc=E52Fj6eX26vrLM:</a> <i>(Kmetijstvo)</i></div>\r\n<div class=\'longlink\'><a href=\'https://www.google.si/search?biw=1366&bih=656&tbm=isch&sa=1&ei=1t-0W7DCFdLVwAKj8Z2wBQ&q=industrija+v+rusiji&oq=industrija+v+rusiji&gs_l=img.3...52428.55620.0.56162.11.11.0.0.0.0.167.1047.7j3.10.0....0...1c.1.64.img..2.0.0....0.yKk8fsZXLqQ#imgrc=4XhHJKFcEbsXuM:\'>https://www.google.si/search?biw=1366&bih=656&tbm=isch&sa=1&ei=1t-0W7DCFdLVwAKj8Z2wBQ&q=industrija+v+rusiji&oq=industrija+v+rusiji&gs_l=img.3...52428.55620.0.56162.11.11.0.0.0.0.167.1047.7j3.10.0....0...1c.1.64.img..2.0.0....0.yKk8fsZXLqQ#imgrc=4XhHJKFcEbsXuM:</a> <i>(Industrija)</i></div>\r\n<div class=\'longlink\'><a href=\'https://www.google.si/search?biw=1366&bih=656&tbm=isch&sa=1&ei=mt-0W6jNMYjOwQKglYLgBA&q=energetika+v+rusiji&oq=energetika+v+rusiji&gs_l=img.3...50159.56254.0.56510.15.15.0.0.0.0.199.1480.13j2.15.0....0...1c.1.64.img..0.1.116...0i7i30k1.0.ZSo9Mxr9iVU#imgrc=st5lL5ttl5zaAM:\'>https://www.google.si/search?biw=1366&bih=656&tbm=isch&sa=1&ei=mt-0W6jNMYjOwQKglYLgBA&q=energetika+v+rusiji&oq=energetika+v+rusiji&gs_l=img.3...50159.56254.0.56510.15.15.0.0.0.0.199.1480.13j2.15.0....0...1c.1.64.img..0.1.116...0i7i30k1.0.ZSo9Mxr9iVU#imgrc=st5lL5ttl5zaAM:</a> <i>(Energetika)</i></div>\r\n<div class=\'longlink\'><a href=\'https://www.google.si/search?biw=1366&bih=656&tbm=isch&sa=1&ei=eN60W8C0M8LFwALDt7zQAQ&q=rudarstvo+rusije&oq=rudarstvo+rusije&gs_l=img.3...283162.285858.0.287458.10.8.0.0.0.0.368.2671.3-8.8.0....0...1c.1.64.img..8.0.0....0.8AIzSFxAtI0#imgrc=73PtTrnNQj34zM:\'>https://www.google.si/search?biw=1366&bih=656&tbm=isch&sa=1&ei=eN60W8C0M8LFwALDt7zQAQ&q=rudarstvo+rusije&oq=rudarstvo+rusije&gs_l=img.3...283162.285858.0.287458.10.8.0.0.0.0.368.2671.3-8.8.0....0...1c.1.64.img..8.0.0....0.8AIzSFxAtI0#imgrc=73PtTrnNQj34zM:</a> <i>(Rudarstvo)</i></div>\r\n<div class=\'longlink\'><a href=\'https://yandex.ru/images/search?pos=17&img_url=http%3A%2F%2Fwww.evening-kazan.ru%2Fsites%2Fdefault%2Ffiles%2Fimages%2Fstories%2F_001.jpg&text=%D1%81%D0%B5%D0%BB%D1%8C%D1%81%D0%BA%D0%BE%D0%B5%20%D0%BD%D0%B0%D1%81%D0%B5%D0%BB%D0%B5%D0%BD%D0%B8%D0%B5&rpt=simage\'>https://yandex.ru/images/search?pos=17&img_url=http%3A%2F%2Fwww.evening-kazan.ru%2Fsites%2Fdefault%2Ffiles%2Fimages%2Fstories%2F_001.jpg&text=%D1%81%D0%B5%D0%BB%D1%8C%D1%81%D0%BA%D0%BE%D0%B5%20%D0%BD%D0%B0%D1%81%D0%B5%D0%BB%D0%B5%D0%BD%D0%B8%D0%B5&rpt=simage</a> <i>(Poselitev)</i></div>\r\n\r\n<b>Moskva:</b>\r\n<div class=\'longlink\'><a href=\'https://sl.wikipedia.org/wiki/Moskva#/media/File:MSK_Collage_2015.png\'>https://sl.wikipedia.org/wiki/Moskva#/media/File:MSK_Collage_2015.png</a></div>\r\n<div class=\'longlink\'><a href=\'https://sl.wikipedia.org/wiki/Moskva#/media/File:Coat_of_Arms_of_Moscow.svg\'>https://sl.wikipedia.org/wiki/Moskva#/media/File:Coat_of_Arms_of_Moscow.svg</a></div>\r\n\r\n<b>Sankt Peterburg:</b>\r\n<div class=\'longlink\'><a href=\'https://sl.wikipedia.org/wiki/Sankt_Peterburg#/media/File:Coat_of_Arms_of_Saint_Petersburg_(2003).svg\'>https://sl.wikipedia.org/wiki/Sankt_Peterburg#/media/File:Coat_of_Arms_of_Saint_Petersburg_(2003).svg</a></div>', '2018-10-29 09:56:15', 'Viri', 'Lednik.jpg');
